$body.on("keyup", "#i_medical_prob_2", function () {

    if ($(this).val() !== "") {

        var inputs = $(this).closest('tr').find('input.form-control');

        inputs.addClass('required');

        inputs.each(function () {
            if ($(this).val() === '') {
                if (!$(this).hasClass('required')) {
                    $(this).addClass('required');
                }
            } else {
                $(this).removeClass('required');
            }
        });

        var inpputs = $(this).closest('tr').find('.required');

        inpputs.each(function () {

            var length = $(this).closest('.form-group').find('.required-label').length;

            if (length === 0) {
                $(this).after('<label id="i_age_onset_1-error" class="error required-label" for="i_age_onset_1">*required</label>');
            }
        });

    } else {

        $(this).closest('tr').find('.form-control').val('');
        $(this).closest('tr').find('.required-label').remove();

    }
});


